#!/usr/bin/env python3\n\"\"\"\nFix all PEG grammar arrows (<-) in the grammar file.\n\"\"\"\n\nimport re\n\ndef fix_peg_arrows():\n    \"\"\"Fix all PEG grammar arrows in the grammar file.\"\"\"\n    file_path = 'docs/spec/grammar.md'\n    \n    with open(file_path, 'r', encoding='utf-8') as f:\n        content = f.read()\n    \n    original_content = content\n    \n    # Fix PEG grammar arrows\n    # Replace <- with &lt;&#45; in grammar rules\n    content = re.sub(r'([A-Za-z][A-Za-z0-9_]*)<-', r'\\1&lt;&#45;', content)\n    \n    if content != original_content:\n        with open(file_path, 'w', encoding='utf-8') as f:\n            f.write(content)\n        print(f\"Fixed PEG arrows in {file_path}\")\n        return True\n    else:\n        print(f\"No changes needed in {file_path}\")\n        return False\n\nif __name__ == '__main__':\n    fix_peg_arrows() 